mapper.select:
select e.id as id, e.no as no, e.host_id as host_id, e.url as url, e.scratch_time as scratch_time, a.id as anime_id, a.name as anime_name from anime_episode_scratch e, anime a where e.anime_id = a.id
and e.status = #{status}
order by e.scratch_time desc, e.anime_id desc ;

mapper.insert:
insert into anime_episode_scratch (
	anime_id, no, url, scratch_time, host_id
) 
values(
	#{episode.anime.id}, #{episode.number} , #{episode.url}, #{episode.scratchTime}, #{episode.hostId}
)

mapper.update:
update anime_episode_scratch
	set status = #{status} 
	where id = #{id}
