mapper.select:
select a.*, b.host_id as host_id, b.alias as host_alias 
		from anime a left JOIN anime_alias b 
		on a.id = b.anime_id  where a.id = #{id}

select * from anime 


		type = #{type}


		and finished = #{finished}

select a.*, b.host_id as host_id, b.alias as host_alias 
	from anime a, anime_alias b 
	where a.id = b.anime_id and a.finished = '0';

mapper.insert:
insert into 
	anime_alias(anime_id, host_id, alias) 
	values(#{alias.animeId},#{alias.hostId}, #{alias.alias});

mapper.update:
update anime set name = #{anime.name}, alias = #{anime.alias},
pic = #{anime.pic},
description = #{anime.description}, publish_month = #{anime.publishMonth}, finished = #{anime.finished}, episode_no = #{anime.episodeNo}, type = #{anime.type} where id = #{anime.id}
